item.description:
S[i] = a[1] + a[2] + ... a[i]
a[l] + ... + a[r] = S[r] - S[l - 1]
#include &lt;iostream&gt;using namespace std; const int N = 100010; int n, m; int a[N], s[N]; int main() { a[0] = 0; s[0] = 0; scanf(&#34;%d%d&#34;, &amp;n, &amp;m); for (int i = 1; i &lt;= n; i++) { scanf(&#34;%d&#34;, &amp;a[i]); } for (int i = 1; i &lt;= n; i++) { s[i] = s[i - 1] + a[i]; } while (m--) { int l, r; scanf(&#34;%d%d&#34;, &amp;r, &amp;l); printf(&#34;%d\n&#34;, s[r] - s[l - 1]); } return 0; }

S[i, j] = 第i行j列格子左上部分所有元素的和
以(x1, y1)为左上角，(x2, y2)为右下角的子矩阵的和为：
S[x2, y2] - S[x1 - 1, y2] - S[x2, y1 - 1] + S[x1 - 1, y1 - 1]
#include &lt;iostream&gt; const int N = 1010; int n, m, q; int a[N][N], s[N][N]; int main() { scanf(&#34;%d%d%d&#34;, &amp;n, &amp;m, &amp;q); for (int i = 1; i &lt;= n; i++) { for (int j = 1; j &lt;= m; j++) { scanf(&#34;%d&#34;, &amp;a[i][j]); } } for (int i = 1; i &lt;= n; i++) { for (int j = 1; j &lt;= m; j++) { s[i][j] = s[i - 1][j] + s[i][j - 1] - s[i - 1][j - 1] + a[i][j]; } } while (q--) { int x1, y1, x2, y2; scanf(&#34;%d%d%d%d&#34;, &amp;x1, &amp;y1, &amp;x2, &amp;y2); printf(&#34;%d\n&#34;, s[x2][y2] - s[x1 - 1][y2] - s[x2][y1 -1] + s[x1 - 1][y1 -1]); } return 0; }

给以(x1, y1)为左上角，(x2, y2)为右下角的子矩阵中的所有元素加上c：
S[x1, y1] += c, S[x2 + 1, y1] -= c, S[x1, y2 + 1] -= c, S[x2 + 1, y2 + 1] += c
#include &lt;iostream&gt;using namespace std; const int N = 1010; int n, m, q; int a[N][N], b[N][N]; void insert(int x1, int y1, int x2, int y2, int c) { b[x1][y1] += c; b[x2 + 1][y1] -= c; b[x1][y2 + 1] -= c; b[x2 + 1][y2 + 1] += c; } int main() { scanf(&#34;%d%d%d&#34;, &amp;n, &amp;m, &amp;q); for (int i = 1; i &lt;= n; i++) { for (int j = 1; j &lt;= m; j++) { scanf(&#34;%d&#34;, &amp;a[i][j]); } } for (int i = 1; i &lt;= n; i++) { for (int j = 1; j &lt;= m; j++) { insert(i, j, i, j, a[i][j]); } } while (q--) { int x1, y1, x2, y2, c; scanf(&#34;%d%d%d%d%d&#34;, &amp;x1, &amp;y1, &amp;x2, &amp;y2, &amp;c); insert(x1, y1, x2, y2, c); } for (int i = 1; i &lt;= n; i++) { for (int j = 1; j &lt;= m; j++) { b[i][j] += b[i - 1][j] + b[i][j - 1] - b[i - 1][j - 1]; } } for (int i = 1; i &lt;= n; i++) { for (int j = 1; j &lt;= m; j++) { printf(&#34;%d &#34;, b[i][j]); } puts(&#34;&#34;); } return 0; }

